// Example TypeScript implementation
import { MCPClient } from '@mcp/client';

const client = new MCPClient({
  apiKey: 'your-api-key'
});

const context = await client.createContext({
  model: 'gpt-4',
  parameters: {
    temperature: 0.7,
    maxTokens: 1000
